import React, { Component } from 'react'
import './index.css'
import LOGO from '../../images/LOGO2.png'
import { Menu,} from 'antd';
// import {  //antd引入
//     HomeOutlined,
//     AppstoreAddOutlined,
//     UserOutlined,
//     BarChartOutlined,
//     SafetyOutlined,
//     MailOutlined,
//     } from '@ant-design/icons';

import menuList from '../../config/menuConfig' //引入动态显示菜单列表，根据数组显示结构
import {Link} from 'react-router-dom'
    
    const { SubMenu } = Menu;

export default class Leftnav extends Component {
    getMenuNodes=(menuList)=>{ // 菜单数组生成对应的标签数组
        return menuList.map(item =>{  //item指数组中的每一个对象---------------------
            if(!item.children){ //如果没有子菜单就返回
                return(
                    <Menu.Item key={item.key} icon={item.icon}>
                            <Link to={item.key}>
                                {item.title}
                            </Link>
                    </Menu.Item>
                )
            }else{
                return(
                    <SubMenu key="sub1" icon={<AppstoreAddOutlined />} title="商品">
                            <Menu.Item key="5">{<MailOutlined />} 品类管理</Menu.Item>
                            <Menu.Item key="6">{<MailOutlined />} 商品管理</Menu.Item>
                    </SubMenu>
                )
            }
        })
    }
    render() {
        return (
            <div className="left-nav">
                <div className="left-nav-header">
                    <img src={LOGO} alt="LOGO" />
                    <h1>矢易科技后台</h1>
                </div>

                <div >
                    {/* <Button type="primary" onClick={this.toggleCollapsed} style={{ marginBottom: 16 }}>
                    {React.createElement(this.state.collapsed ? MenuUnfoldOutlined : MenuFoldOutlined)}
                    </Button> */}
                    <Menu
                    defaultSelectedKeys={['1']} //指定默认选中打开
                    // defaultOpenKeys={['sub1']}  //指定默认选中打开
                    mode="inline"
                    theme="dark"
                    >       
                    {/* 动态生成的菜单--------------------------------------------------------------- */}
                            {
                                this.getMenuNodes(menuList) //调用动态生成菜单函数
                            }
                        {/* 下面是写死情况----------------------------------------------------- */}
                        {/* <Menu.Item key="1" icon={<HomeOutlined />}>
                            <Link to='/admin/home'>
                                首页
                            </Link>
                        </Menu.Item> */}

                        {/* <SubMenu key="sub1" icon={<AppstoreAddOutlined />} title="商品">
                            <Menu.Item key="5">{<MailOutlined />} 品类管理</Menu.Item>
                            <Menu.Item key="6">{<MailOutlined />} 商品管理</Menu.Item>
                        </SubMenu> */}

                        {/* <Menu.Item key="2" icon={<UserOutlined />}>
                            <Link to='/admin/user'>
                                用户管理
                            </Link>
                        </Menu.Item>

                        <Menu.Item key="3" icon={<SafetyOutlined />}>
                            <Link to='/admin/role'>
                                角色管理
                            </Link>
                        </Menu.Item>

                        <SubMenu key="sub2" icon={<BarChartOutlined />} title="数据可视化">
                            <Menu.Item key="9">Option 9</Menu.Item>
                            <Menu.Item key="10">Option 10</Menu.Item>
                            
                            <SubMenu key="sub3" title="Submenu">
                            <Menu.Item key="11">Option 11</Menu.Item>
                            <Menu.Item key="12">Option 12</Menu.Item>
                            </SubMenu>
                        </SubMenu> */}
                    </Menu>
                </div>

                
            </div>
        )
    }
}
