import React, { Component } from 'react'
import {Card,Button,Table} from 'antd'
import {PlusOutlined} from '@ant-design/icons';
import LinkButton from '../../Link-Button';
import {reqCategorys} from '../../../api/index'
export default class Category extends Component {
    state={
        loading: false, //等待数据的转圈状态
        categorys:[],
    }

    getCategorys=async()=>{
        this.setState({loading:true})
        const result =await reqCategorys('0')
        console.log('返回的结果',result)
        this.setState({loading:false})
        if(result.data.status===0){
            const data = result.data.data  //ajax请求来的数据数组
            this.setState({categorys:data})
        }else{
            alert('请求失败')
        }
    }
    componentWillMount(){
        this.columns = [
            {
            title: '分类名称',
            dataIndex: 'name',
            key: 'name',
            },
            {
            title: '操作',
            width:300,
            render:()=>( //返回需要的标签。由antd定义
                <span>
                    <LinkButton>修改分类</LinkButton>&nbsp;&nbsp;
                    <LinkButton>查看子分类</LinkButton>
                </span>
                )
            },
        ];
    }
    componentDidMount(){
        this.getCategorys() //组件加载发送ajax请求列表
    }
    render() {
        const title ='一级分类列表'
        const extra =<Button type="primary"><PlusOutlined />添加</Button> 
        const {loading} = this.state
        return (
            <Card title={title} extra={extra} style={{width: '100%',height: '100%'}}>
                <Table
                    dataSource={this.state.categorys}
                    columns={this.columns}
                    bordered
                    rowKey='_id'
                    pagination={{defaultPageSize:'4',showQuickJumper:'ture'}}
                    loading={loading}  //配置分页器
                    />
            </Card>
        )
    }
}
