import React, { Component } from 'react'
import {Card,Select,Input,Button,Table} from 'antd'
const Option =Select.Option
export default class Product extends Component {
    state={
        products:[],//商品的数组
    }
    render() {
        const {products} = this.state
            const dataSource = [
                {
                key: '1',
                name: '胡彦斌',
                age: 32,
                address: '西湖区湖底公园1号',
                },
                {
                key: '2',
                name: '胡彦祖',
                age: 42,
                address: '西湖区湖底公园1号',
                },
            ];
            
            const columns = [
                {
                title: '姓名',
                dataIndex: 'name',
                key: 'name',
                },
                {
                title: '年龄',
                dataIndex: 'age',
                key: 'age',
                },
                {
                title: '住址',
                dataIndex: 'address',
                key: 'address',
                },
            ];


        const title=(
            <span>
                <Select value='1' style={{width:150}}> 
                    <Option value='1'>按名称搜索</Option>
                    <Option value='2'>按描述搜索</Option>
                </Select>&nbsp;&nbsp;
                <Input placeholder="请输入关键字" style={{width:150}}/>
                <Button type='primary'>搜索</Button>
            </span>
        )
        const extra=(
            <Button type='primary'>
                添加商品
            </Button>
        )
        return (
            <Card title={title} extra={extra}>
                <Table
                bordered
                    dataSource={products} columns={columns} //展示的数据
                />
            </Card>
        )
    }
}
