import React, { Component } from 'react'
import { Button,Card,Table, } from 'antd'
import {PAGE_SIZE} from '../../../utils/constants'

export default class Role extends Component {
    state={
        roles: [
            {
                    "menus":[
                        "/home",
                        "/role",
                        "/products",
                        "/category",
                        "/product"
                ],
                "_id":"11",
                "name":"经理",
                "create_time":15546256542,
                "_v":0,
                "auth_time":15546256542,
                "auth_name":"admin"
            },
            {
                "menus":[
                    "/home",
                    "/role",
                    "/products",
                    "/category",
                    "/product"
            ],
            "_id":"22",
            "name":"经理",
            "create_time":15546256542,
            "_v":2,
            "auth_time":15546256542,
            "auth_name":"admin"
        },
        {
            "menus":[
                "/home",
                "/role",
                "/products",
                "/category",
                "/product"
        ],
        "_id":"33",
        "name":"销售",
        "create_time":15546256542,
        "_v":0,
        "auth_time":15546256542,
        "auth_name":"admin"
    },
        ]
    }

    initColumn=()=>{
        this.columns=[
            {
                title: '角色名称',
                dataIndex:'name'
            },
            {
                title: '创建时间',
                dataIndex:'create_time'
            },
            {
                title: '授权时间',
                dataIndex:'auth_time'
            },
            {
                title: '授权人',
                dataIndex:'auth_name'
            },

        ]
    }

    onRow=(role)=>{
        return{
            onClick:event =>{ //监听用户点击的哪一行
                alert('航航')
            }
        }
    }



    componentWillMount(){
        this.initColumn()
    }

    
    render() {
        const{roles} = this.state;
        const titile=(
            <span>
                <Button type="primary">创建角色</Button>&nbsp;
                <Button type="primary" disabled>设置角色权限</Button>
            </span>
        )
        return (
            <Card title={titile}>
                <Table
                    bordered
                    rowKey='_id'
                    dataSource={roles}
                    columns={this.columns}
                    pagination={{defaultPageSize:PAGE_SIZE}}
                    rowSelection={{type: 'radio'}} //设置可以单选
                    onRow={this.onRow}//监听用户点击的哪一行
                
                
                
                />

            </Card>
        )
    }
}
