//此文件用户维持登陆、用户登陆登出
import store from 'store'  //第三方库，用于删除用户，维持登陆
const USER_KEY=user_key;
export default {
    //保存用户
    saveUser(user){
        store.set(user_key, user)
    },
    //读取用户
    getUser(){
        return store.get(user_key)||{}
    },
    //删除用户，退出登陆
    removeUser(){
        store.remove(user_key)
    }
}



