import React, { Component } from 'react'
import {Card,Button,Table} from 'antd'
import {PlusOutlined} from '@ant-design/icons';
import LinkButton from '../../Link-Button';
import {reqCategorys} from '../../../api/index'
export default class Category extends Component {
    state={
        categorys:[
            {
                "parentId": "0",
                "_id": "5e12b8bce31bb727e4b0e348",
                "name": "家用电器",
                "__v": 0
            },
            {
                "parentId": "0",
                "_id": "5e130e60e31bb727e4b0e34b",
                "name": "手机",
                "__v": 0
            },
            {
                "parentId": "0",
                "_id": "5e130ec7e31bb727e4b0e34c",
                "name": "洗衣机",
                "__v": 0
            },
            {
                "parentId": "0",
                "_id": "5e1346533ed02518b4db0cd7",
                "name": "图书",
                "__v": 0
            },
            {
                "parentId": "0",
                "_id": "5e13467e3ed02518b4db0cd8",
                "name": "杯具",
                "__v": 0
            },
        ],
    }

    getCategorys=async()=>{
        const result =await reqCategorys(0)
        console.log(result)
        if(result.data.status===200){
            // alert('列表请求发送成功')
        }
    }
    componentWillMount(){
        this.columns = [
            {
            title: '分类名称',
            dataIndex: 'name',
            key: 'name',
            },
            {
            title: '操作',
            width:300,
            render:()=>( //返回需要的标签。由antd定义
                <span>
                    <LinkButton>修改分类</LinkButton>&nbsp;&nbsp;
                    <LinkButton>查看子分类</LinkButton>
                </span>
                )
            },
        ];
    }
    componentDidMount(){
        this.getCategorys() //组件加载发送ajax请求列表
    }
    render() {
        const title ='一级分类列表'
        const extra =<Button type="primary"><PlusOutlined />添加</Button> 
            
        return (
            <Card title={title} extra={extra} style={{width: '100%',height: '100%'}}>
                <Table
                    dataSource={this.state.categorys} columns={this.columns}
                    bordered
                    rowKey='_id'
                    
                    />
            </Card>
        )
    }
}
