import React, { Component } from 'react'
import {Card,Select,Input,Button,Table} from 'antd'
import LinkButton from '../../Link-Button'
import reqProducts from '../../../api'
const Option =Select.Option
export default class Product extends Component {
    state={
        products:[],//商品的数组
    }

    initColumns=()=>{//初始化Table列的数组
        this.columns = [
            {
                title: '商品名称',
                dataIndex: 'name',
            },
            {
                title: '商品描述',
                dataIndex: 'desc',
            },
            {
                title: '价格',
                dataIndex: 'price',
                render:(price)=> //加人名币符号
                    '¥'+price
            },
            {
                title: '状态',
                dataIndex: 'status',
                render:(status)=>{//加人名币符号 相当于after ：：before作用
                    return(
                        <span>
                            <Button type='primary'>下架</Button>
                            {/* 当前商品的状态↓ */}
                            <span>在售</span>  
                        </span>
                    )
                
                } 
            },
            {
                title: '操作',
                render:(product)=>{//加人名币符号 相当于after ：：before作用
                    return(
                        <span>
                            <LinkButton>详情</LinkButton>
                            <LinkButton>修改</LinkButton>
                        </span>
                    )
                
                } 
            },
            
        ];
    }

    getProducts=async()=>{
        const result=await reqProducts(1,1)
    }



    componentWillMount(){
        this.initColumns()
    }
    componentDidMount(){
        this.getProducts()
    }
    render() {
        const {products} = this.state
            const dataSource = [
                {
                key: '1',
                name: '胡彦斌',
                age: 32,
                address: '西湖区湖底公园1号',
                },
                {
                key: '2',
                name: '胡彦祖',
                age: 42,
                address: '西湖区湖底公园1号',
                },
            ];
            
            


        const title=(
            <span>
                <Select value='1' style={{width:150}}> 
                    <Option value='1'>按名称搜索</Option>
                    <Option value='2'>按描述搜索</Option>
                </Select>&nbsp;&nbsp;
                <Input placeholder="请输入关键字" style={{width:150}}/>
                <Button type='primary'>搜索</Button>
            </span>
        )
        const extra=(
            <Button type='primary'>
                添加商品
            </Button>
        )
        return (
            <Card title={title} extra={extra}>
                <Table
                rowKey='_id'
                bordered
                    dataSource={products} columns={this.columns} //展示的数据
                />
            </Card>
        )
    }
}
