import React, { Component } from 'react'
import { Button,Card,Modal,Table, } from 'antd'
import {PAGE_SIZE} from '../../../utils/constants'
import { reqRoles } from '../../../api'
import AddForm from './add-form'
import {reqAddRole} from '../../../api' //添加角色

export default class Role extends Component {
    state={
        roles: [],
        role:[],//当我选中列表中要加权限的用户
        isShowAdd:false,//是否显示添加角色界面， 默认不显示
    }
    getRoles= async ()=>{
        
        const result =await reqRoles()
        // console.log(result)
        if(result.data.status===0){//角色列表请求成功
            // alert('角色请求成功')
            const roles = result.data
        }else{
            alert('请求失败')
        }
    }

    initColumn=()=>{
        this.columns=[
            {
                title: '角色名称',
                dataIndex:'name'
            },
            {
                title: '创建时间',
                dataIndex:'create_time'
            },
            {
                title: '授权时间',
                dataIndex:'auth_time'
            },
            {
                title: '授权人',
                dataIndex:'auth_name'
            },

        ]
    }

    onRow=(role)=>{  //选中的要加权限的用户
        return{
            onClick:event =>{ //监听用户点击的哪一行
                this.setState({role})
            }
        }
    }

    //请求所有角色
    getRoles=async()=>{
        const result = await reqRoles()
        if(result.data.status===0){//角色请求成功
            const roles = result.data.data //取出角色数组
            this.setState({roles})
        }
        console.log('请求角色的数据：',result.data.data)
        console.log(this.state.roles)
    }
    //添加角色----------
    addRole=async()=>{
        // const result = await reqAddRole()
        
        // const value = this.form.getFieldsValue()
        // console.log('我是子组件传递过来的',value)
    }


    componentWillMount(){
        this.initColumn()
    }

    componentDidMount(){
        this.getRoles()
    }

    
    render() {
        const{roles,role,isShowAdd} = this.state;
        const titile=(
            <span>
                <Button type="primary" onClick={()=>{this.setState({isShowAdd: true})}}>创建角色</Button>&nbsp;
                <Button type="primary" disabled={!role._id}>设置角色权限</Button>
            </span>
        )
        return (
            <Card title={titile}>
                <Table
                    bordered //设置表格边框
                    rowKey='_id'  //设置每一行的id
                    dataSource={roles} 
                    columns={this.columns}
                    pagination={{defaultPageSize:PAGE_SIZE}} //设置一行默认显示多少条内容
                    rowSelection={{type: 'radio',selectedRowKeys:[role._id]}} //设置可以单选
                    onRow={this.onRow}//监听用户点击的哪一行
                
                />

                <Modal
                    title='添加角色'
                    visible={isShowAdd}  //这个表单是否可见根据这个值判断
                    onOk={this.addRole} //点击确认发送请求添加角色
                    onCancel={()=>{this.setState({isShowAdd:false})}}//取消就隐藏添加角色界面
                >
                    <AddForm  //添加角色的组件界面
                        setForm={(form)=>this.form = form}  //传给子组件一个回调函数,从而得到 子组件的form对象！！！！！！--------------------------------------------存到this中
                    />


                </Modal>

            </Card>
        )
    }
}
