import React, { Component } from 'react'
import { Button,Card,Table, } from 'antd'
import {PAGE_SIZE} from '../../../utils/constants'
import { reqRoles } from '../../../api'

export default class Role extends Component {
    state={
        roles: [],
        role:[],//当我选中列表中要加权限的用户
    }
    getRoles= async ()=>{
        
        const result =await reqRoles()
        // console.log(result)
        if(result.data.status===0){//角色列表请求成功
            // alert('角色请求成功')
            const roles = result.data
        }else{
            alert('请求失败')
        }
    }

    initColumn=()=>{
        this.columns=[
            {
                title: '角色名称',
                dataIndex:'name'
            },
            {
                title: '创建时间',
                dataIndex:'create_time'
            },
            {
                title: '授权时间',
                dataIndex:'auth_time'
            },
            {
                title: '授权人',
                dataIndex:'auth_name'
            },

        ]
    }

    onRow=(role)=>{  //选中的要加权限的用户
        return{
            onClick:event =>{ //监听用户点击的哪一行
                this.setState({role})
            }
        }
    }

    //请求所有角色
    getRoles=async()=>{
        const result = await reqRoles()
        if(result.data.status===0){//角色请求成功
            const roles = result.data.data //取出角色数组
            this.setState({roles})
        }
        console.log('请求角色的数据：',result.data.data)
        console.log(this.state.roles)
    }

    componentWillMount(){
        this.initColumn()
    }

    componentDidMount(){
        this.getRoles()
    }

    
    render() {
        const{roles,role} = this.state;
        const titile=(
            <span>
                <Button type="primary">创建角色</Button>&nbsp;
                <Button type="primary" disabled={!role._id}>设置角色权限</Button>
            </span>
        )
        return (
            <Card title={titile}>
                <Table
                    bordered //设置表格边框
                    rowKey='_id'  //设置每一行的id
                    dataSource={roles} 
                    columns={this.columns}
                    pagination={{defaultPageSize:PAGE_SIZE}} //设置一行默认显示多少条内容
                    rowSelection={{type: 'radio',selectedRowKeys:[role._id]}} //设置可以单选
                    onRow={this.onRow}//监听用户点击的哪一行
                
                
                
                />

            </Card>
        )
    }
}
